import React from "react";
import ReactDOM from 'react-dom';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

const shops = [
  {
    id:1,
    name: "TEST",
    date: "20/11/2002",
    link: "https://youtube.com/"
  },
  {
    id:2,
    name:"TEST2",
    link: "https://youtube.com/"
  },
  {
    id: 3,
    name:"TEST3",
    link: "https://youtube.com/"
  },
  {
    id: 4,
    name:"TEST4",
    link: "https://youtube.com/"
  }
]

export default function App() {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
          </ul>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
            {shops.map (shop => {
              return PersonalUsers(shop)
            })}
          <Route path="/about">
            <About />
          </Route>
          <Route path="/">
            <Home />
          </Route>
          {/*<div>
            {shops.map (shop => {
              return PersonalUsers(shop)
            })}
          </div>*/}
        </Switch>
      </div>
    </Router>
  );
}

function PersonalUsers(shop)
{
  return(
      <Route path={`/users/${shop.id}`}>
        {Users(shop)}
      </Route>
  )
}

function DrawHome(shops) {
  return (
    <React.Fragment>
      <Table size="small">
        <TableHead>
          <TableRow>
            <TableCell>Date</TableCell>
            <TableCell>Name</TableCell>
            <TableCell>Ship To</TableCell>
            <TableCell>Payment Method</TableCell>
            <TableCell align="right">Sale Amount</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {shops.map((shop) => (
            <TableRow key={shop.id}>
              <TableCell>{shop.date}</TableCell>
              <TableCell>{shop.name}</TableCell>
              <TableCell>{shop.shipTo}</TableCell>
              <TableCell>{shop.paymentMethod}</TableCell>
              <TableCell align="right">{shop.amount}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </React.Fragment>
  )
}

function Home() {
  return (
    <div className="MainPage">
      {DrawHome(shops)}
    </div>
  )
}

function About() {
  return <h2>About</h2>;
}

function Users(shop) {
  return (
    <div>
      <h2>NEW USER</h2>
      <h2>{shop.name}</h2>
      <h2>{shop.link}</h2>
    </div>
  )
}

// ========================================

ReactDOM.render(
    App(),
    document.getElementById('root')
);
